import React from 'react'

import './styles/Badges.css'
import confLogo from '../images/badge-header.svg'
import Navbar from '../components/Navbar'

class Badges extends React.Component{
    state={
        data:[
            {
                firstName:'Daniela',
                lastName:'Gallegos',
                twitter:'djgallegos',
                jobTitle:'Designer',
                email:'gallegosdaniela16@hotmail.com',
                avatarUrl:"https://www.gravatar.com/avatar/HASH"
            },
            {
                firstName:'Jose',
                lastName:'Gallegos',
                twitter:'jagallegos',
                jobTitle:'Debeloper Backend',
                email:'gallegosjose@hotmail.com',
                avatarUrl:"https://www.gravatar.com/avatar/HASH"
            },
            {
                firstName:'Michael',
                lastName:'Morales',
                twitter:'mmorales',
                jobTitle:'Developer',
                email:'moralesmichael@hotmail.com',
                avatarUrl:"https://www.gravatar.com/avatar/HASH"
            }
        ]
    }
    render(){
        return(
            <div>
                <Navbar />

                <div className="Badges">
                    <div className="Badges__hero">
                        <div className="Badges__container">
                            <img className="Badges_conf-logo" src={confLogo} alt="Conf logo"/>
                        </div>
                    </div>
                </div>
                <div className="Badges__container">
                    <div className="Badges__buttons">
                        <a href="/badges/new" className="btn btn-primary">New Badge</a>
                    </div>

                    <div className="Badge__list">
                        <div className="Badge__container">
                            <div className="list-unstyled">
                                {this.state.data.map((badge) =>{
                                    return (
                                        <li key={badge.id} >
                                            <p>
                                                {badge.firstName} {badge.lastName}
                                            </p>
                                        </li>
                                    )
                                })}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }



}

export default Badges;
